function setupVideo(){var e=document.getElementById("video"),t=e.querySelector(".video__link"),r=e.querySelector(".video__media"),a=e.querySelector(".video__button"),i=parseMediaURL(r);e.addEventListener("click",function(){var r=createIframe(i);t.remove(),a.remove(),e.appendChild(r)}),t.removeAttribute("href"),e.classList.add("video--enabled")}function parseMediaURL(e){return e.src.match(/https:\/\/i\.ytimg\.com\/vi\/([a-zA-Z0-9_-]+)\/maxresdefault\.jpg/i)[1]}function createIframe(e){var t=document.createElement("iframe");return t.setAttribute("allowfullscreen",""),t.setAttribute("allow","autoplay"),t.setAttribute("src",generateURL(e)),t.classList.add("video__media"),t}function generateURL(e){return"https://www.youtube.com/embed/"+e+"?rel=0&showinfo=0&autoplay=1"}document.addEventListener("DOMContentLoaded",function(){setupVideo()});